import React from 'react';
import Card from './UI/Card';
import Button from './UI/Button';
import './FeedbackForm.css';
import { useState } from 'react';
import RatingSelect from './RatingSelect';
const FeedbackForm = () => {
    const [text , setText] = useState('');
    const [ btnDisabled , setButtonDisabled] = useState(true);
    const [ message , setMessage] = useState('');
    const [rating , setRating] = useState('');
    const inputChangeHandler = (e) => {
//         console.log(e.target.value);
// console.log(+(e.target.value))

        if(text === ''  && text !== +(e.target.value)  ) {
setButtonDisabled(true);
setMessage(null);
        } else if(text !== '' && text.trim().length <= 10 && text !== +(e.target.value)) {
            setButtonDisabled(true);
            setMessage('Input must not be Numbers and must be at least 10 characters long')
        }else if(text !== '') {
            setMessage(null);
            setButtonDisabled(false);
            
        }
        setText(e.target.value);
       
    }
    const selectHandler = (rating) => {
        console.log(rating);
setRating(rating);
    }

    // submitting the form

    const submitHandler = (e) => {
        e.preventDefault();
        const newFeedback = {
        text,
        rating,
        id: Math.random().toS
        }
    }
  return <Card>
      <form className='feedback-form' onSubmit = {submitHandler}>
      <h3>How would you rate your service with us ? </h3>
      <div className='ratingSelect'>
      <RatingSelect onSelect = {selectHandler} select = {rating}/>
      </div>
    
      <div className='input-group'>
          <input type="text" placeholder = "Write a review" onChange = {inputChangeHandler} value={text} />
        <Button type = "submit" isDisabled={btnDisabled} >Send</Button>

      </div>
      {message && <p className='message'>{message}</p>}
      </form>
   

  </Card>;
};

export default FeedbackForm;
