//   },
// ]

const App = () => {
  const [feedback , setFeedback] = useState(FeedbackData);

const addItemHandler = (newFeedback) => {
  setFeedback((prevFeed) => {
    return [newFeedback , ...prevFeed];
  })
}

  const deleteItemHandler = (id) => {
if(window.confirm('Are you sure you want to delete?')){
  setFeedback(feedback.filter((item) => item.id !== id));

}

  }


  return (
    <div className="App">
      <Router>
       <Header />
       <Routes>
         <Route>
         <FeedbackForm onSubmitHandler = {addItemHandler}/>
       <FeedbackStats feedback = {feedback} />
       {/* {feedback.length === 0 ? <p>No Reviews Yet, Maybe add one ? </p> : 
       <FeedbackList feedback = {feedback} handleDelete = {deleteItemHandler} />
       } */}
      {feedback.length > 0 && <FeedbackList feedback = {feedback} handleDelete = {deleteItemHandler} /> }
      {feedback.length === 0 && <p style= {{textAlign :'center'}}>No Reviews Yet, Maybe add one ?</p>}
         </Route>
       </Routes>

     <Route path= '/about' component = {About} />
      </Router>

    </div>
  );
}

export default App;
