import React from 'react';
import './RatingSelect.css';
import { useState } from 'react';
const RatingSelect = ({onSelect , select}) => {
    const [selected , setSelected] = useState(10);

    const handleChange = (e) => {
// console.log(typeof +e.currentTarget.value);
setSelected(+e.currentTarget.value);
// onSelect(+e.currentTarget.value);
onSelect(select)
    }
  return<ul className='rating'>
  <li>
    <input
      type='radio'
      id='num1'
      name='rating'
      value='1'
      onChange={handleChange}
    //   checked={selected === 1}
    checked = {select}
    />
    <label htmlFor='num1'>1</label>
  </li>
  <li>
    <input
      type='radio'
      id='num2'
      name='rating'
      value='2'
      onChange={handleChange}
    //   checked={selected === 2}
    checked = {select}
    />
    <label htmlFor='num2'>2</label>
  </li>
  <li>
    <input
      type='radio'
      id='num3'
      name='rating'
      value='3'
      onChange={handleChange}
    //   checked={selected === 3}
    checked = {select}
    />
    <label htmlFor='num3'>3</label>
  </li>
  <li>
    <input
      type='radio'
      id='num4'
      name='rating'
      value='4'
      onChange={handleChange}
    //   checked={selected === 4}
    checked = {select}
    />
    <label htmlFor='num4'>4</label>
  </li>
  <li>
    <input
      type='radio'
      id='num5'
      name='rating'
      value='5'
      onChange={handleChange}
    //   checked={selected === 5}
    checked = {select}
    />
    <label htmlFor='num5'>5</label>
  </li>
  <li>
    <input
      type='radio'
      id='num6'
      name='rating'
      value='6'
      onChange={handleChange}
    //   checked={selected === 6}
    checked = {select}
    />
    <label htmlFor='num6'>6</label>
  </li>
  <li>
    <input
      type='radio'
      id='num7'
      name='rating'
      value='7'
      onChange={handleChange}
    //   checked={selected === 7}
    checked = {select}
    />
    <label htmlFor='num7'>7</label>
  </li>
  <li>
    <input
      type='radio'
      id='num8'
      name='rating'
      value='8'
      onChange={handleChange}
    //   checked={selected === 8}
    checked = {select}
    />
    <label htmlFor='num8'>8</label>
  </li>
  <li>
    <input
      type='radio'
      id='num9'
      name='rating'
      value='9'
      onChange={handleChange}
    //   checked={selected === 9}
    checked = {select}
    />
    <label htmlFor='num9'>9</label>
  </li>
  <li>
    <input
      type='radio'
      id='num10'
      name='rating'
      value='10'
      onChange={handleChange}
    //   checked={selected === 10}
    checked = {select}
    />
    <label htmlFor='num10'>10</label>
  </li>
</ul>
};

export default RatingSelect;
